import React from 'react';
import { Loading } from '../components/Loading';
import { FormUpdatePasien } from '../containers/FormUpdatePasien';
import { Appointment } from '../utils/Appointment';


export class UpdatePasien extends React.Component {

	constructor(props) {
		super(props)
		this.state = {
			loading: true,
			pasien: {},
		}
		this.handleFormSubmit = this.handleFormSubmit.bind(this)
	}

	componentDidMount() {
		Appointment.getDetailPasien(this.props.match.params.id).then( response => {
			console.warn(response);
			if (response.status === 200) {
				this.setState({
					loading: false,
					pasien: response.result,
				})
			} else {
				alert('Data tidak ditemukan')
				this.props.history.push('/all-pasien')
			}
		})
	}

	handleFormSubmit(e) {
		e.preventDefault()
		this.setState({
			loading: true
		})

		const data = new FormData(e.target)
		const dataJson = {}

		console.log(data);

		data.forEach( (val, key) => {
			if (val !== "") {
				let name = key.split('.');
				if (name.length > 1) {
					let last = name.pop()
					console.log(`last == ${last}`)
					name.reduce( (prev, next) => {
						console.log(`prev[next] = ${prev[next]}`)
						console.log(`prev[next] || {} = ${prev[next] || {}}`)
						return prev[next] = prev[next] || {};
					}, dataJson)[last] = val

				} else {
					dataJson[key] = val
				}
			}
		})

		Appointment.updateStatusPasien(dataJson).then( response => {
			// console.warn(`dataaaa = ${dataJson}`)
			console.log(dataJson);
			if (response.status === 200) {
				this.setState({
					loading: false,
					pasien: response.result
				})
				alert(`Suskses update pasien ${this.state.pasien.nama}`)
			} else {
				this.setState({
					loading: false,
				})
				alert(`Gagal update pasien ${this.state.pasien.nama}`)
			}
		})
	}

	render() {
		if (this.state.loading) {
			return (
				<Loading msg="Fetching Data..." />
			)
		} else {
			return (
				<FormUpdatePasien pasien={this.state.pasien} onSubmit={this.handleFormSubmit} />
			)
		}
	}
}
